    class Solution {
        public List<List<Integer>> threeSum(int[] nums) {
            List<List<Integer>> result = new ArrayList<>();
            // å…ˆæ’åºå‡åº
            Arrays.sort(nums);
            //æœ€å¤–å±‚æ­£å¸¸éå†å³å¯
            for (int i = 0; i < nums.length; i++) {
                //åªæœ‰å’Œä¸Šä¸€æ¬¡ä¸åŒçš„å…ƒç´ æ‰è¿›è¡Œæšä¸¾
                if (i > 0 && nums[i] == nums[i - 1]) {
                    continue;
                }
                //    ç¬¬äºŒå±‚æ‰¾ä¸€ä¸ªä¸å°äºæœ€å¤–å±‚å…ƒç´ çš„å€¼å¼€å§‹éå†,ä»å·¦å¾€å³éå†
                for (int j = i + 1; j < nums.length; j++) {
                    // åªæœ‰å’Œä¸Šä¸€æ¬¡ä¸åŒçš„æ‰æšä¸¾
                    if (j > i + 1 && nums[j] == nums[j - 1]) {
                        continue;
                    }
                    //    ç¬¬ä¸‰å±‚,ä»å³å¾€å·¦éå†
                    int k = nums.length - 1;
                    while (j < k && nums[j] + nums[k] > 0 - nums[i]) {
                        k--;
                    }
                    if (j == k) {
                        break;
                    }

                    if (nums[i] + nums[j] + nums[k] == 0) {
                        int finalI = i;
                        int finalJ = j;
                        int finalK = k;
                        result.add(new ArrayList<Integer>() {{
                            add(nums[finalI]);
                            add(nums[finalJ]);
                            add(nums[finalK]);
                        }});
                    }
                }
            }
            return result;
        }
    }
//leetcode submit region end(Prohibit modification and deletion)

